package starter;

public class Pattern5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i,j,p,k=0;
		for(i=1;i<=5;i++) {

			for(j=1;j<=i;j++)
			{
			System.out.print(i+" ");
			
		}
			for(p=2+k;p<=5;p++)
			{
				System.out.print(p+" ");
			}
			System.out.println();
			k++;
			
	}

}
}
